var sent=false;$("#leadform").on("submit",function(event){event.preventDefault();console.log("HERE!");var error=false;if(!$("input.name").val()){$("input.name").addClass("error");error=true}if(!$("input.email").val()){$("input.email").addClass("error");error=true}if(error||sent){return false}else{$.ajax({url:"https://formspree.io/manuel@summer.ai",method:"POST",data:{name:$("input.name").val(),company:$("input.company").val(),email:$("input.email").val()},dataType:"json",success:function(){sent=true;$("#leadform .inner").replaceWith("<p class='ok'>Thanks - we'll be in touch in the next 24 hours.</p>")},error:function(){$("#leadform .inner").replaceWith("<p class='ok error'>Oops - something went wrong. Please get in touch with us directly.</p>")}})}return false});
